const boring = require('boringbits');

const dynamoose = require('dynamoose');

if (boring.config.get('connections.dynamodb.local') === true) {
  dynamoose.local();
}

dynamoose.AWS.config.update({
  accessKeyId: boring.config.get('connections.dynamodb.accessKeyId'),
  secretAccessKey: boring.config.get('connections.dynamodb.secretAccessKey'),
  region: boring.config.get('connections.dynamodb.region')
});

module.exports = dynamoose;