const dynamoose = require('dynamoose');

const Schema = dynamoose.Schema;
var ContentSchema = new Schema({
  biz_uuid: {
    type: String,
    hashKey: true
  },
  name: {
    type: String
  },
  image: {
    type: String
  },
  social: {
    type: Object
  }
}, {
  throughput: {
    read: 5,
    write: 2
  },
  timestamps: {
    createdAt: 'creationDate',
    updatedAt: 'lastUpdateDate'
  }
});


module.exports = {
  schema: ContentSchema
}