const dynamoose = require('dynamoose');

const Schema = dynamoose.Schema;
var ContentSchema = new Schema({
  biz_uuid: {
    type: String,
    hashKey: true
  },
  name: {
    type: String
  },
  address: {
    type: String
  },
  status: {
    type: String
  },
  image: {
    type: String
  },
  social: {
    type: Array
  },
  user_count: {
    type: Number
  },

}, {
  throughput: {
    read: 5,
    write: 2
  },
  timestamps: {
    createdAt: 'creationDate',
    updatedAt: 'lastUpdateDate'
  }
});


module.exports = {
  schema: ContentSchema
}