const Schema = dynamoose.Schema;
var ContentSchema = new Schema({
  content_uuid: {
    type: String,
    hashKey: true
  },
  hashtags: {
    type: Array
  },
  fileLocation: {
    type: String
  },
  location: {
    type: Object 
  },
  biz_uuid: {
    type: String,
    index: {
      global: true,
      rangeKey: 'content_uuid',
      name: 'biz_uuid',
      project: true, // ProjectionType: ALL
      throughput: 5 // read and write are both 5
    }
  },
  updateDate: {
    type: String
  },
  uploadTime: {
    type: String
  },
  likes: {
    type: String
  },
  shares: {
    type: String
  },
  comments: {
    type: Object
  }
}, {
  throughput: {
    read: 5,
    write: 2
  },
  timestamps: {
    createdAt: 'creationDate',
    updatedAt: 'lastUpdateDate'
  }
});


module.exports = {
  schema: ContentSchema
}