const dynamoose = require('dynamoose');

const Schema = dynamoose.Schema;
var UserSchema = new Schema({
  user_uuid: {
    type: String,
    hashKey: true
  },
  provider_user_id: {
    type: String,
    required: true,
    index: {
      global: true,
      rangeKey: 'user_uuid',
      name: 'user_id_index',
      project: true, // ProjectionType: ALL
      throughput: 5 // read and write are both 5
    }
  },
  name : {
    type: String
  },
  roles: {
    
  }
}, {
  throughput: {
    read: 5,
    write: 2
  },
  timestamps: {
    createdAt: 'creationDate',
    updatedAt: 'lastUpdateDate'
  }
});


module.exports = {
  schema: UserSchema
}